// CharacterSheet.tsx
import React, { useState } from 'react';
import { useCharacterSheetStore } from './store';
import { Attribute } from './types';
import { calculateDiceResult } from './dice';

const diceSize = { width: '40px', height: '40px', fontSize: '20px' };

export const CharacterSheet = () => {
  const { data, updateAttribute, updateData } = useCharacterSheetStore();
  const [editAttributes, setEditAttributes] = useState(false);
  const hasMilitaryBackpack = data.equipment.includes('Military Backpack');

  const strength = data.attributes.FOR || 0;
  const baseLoad = strength === 0 ? 2 : strength * 5;
  const maxLoad = baseLoad + (hasMilitaryBackpack ? 2 : 0);

  const [rollResult, setRollResult] = useState(0);

  const handleRoll = (dieSize: number) => {
    const result = calculateDiceResult(dieSize);
    setRollResult(result);
  };

  return (
    // ... character sheet UI ...
    <div>
      {/* ... Attributes section ... */}
      <div>
        <label>Strength (FOR):</label>
        <input
          type="number"
          value={data.attributes.FOR || ''}
          onChange={(e) => updateAttribute('FOR', parseInt(e.target.value))}
        />
        <br />
        <label>Base Load:</label>
        <span>{baseLoad}</span>
        <br />
        <label>Max Load:</label>
        <span>{maxLoad}</span>
        <br />
        {/* ... Equipment section ... */}
        {/* ... Stats section ... */}
        {/* ... Dice roll inputs ... */}
        <button onClick={() => handleRoll(20)} style={{ ...diceSize, marginLeft: '5px' }}>
          D20
        </button>
        <button onClick={() => handleRoll(12)} style={diceSize}>
          D12
        </button>
        <button onClick={() => handleRoll(10)} style={diceSize}>
          D10
        </button>
        <button onClick={() => handleRoll(8)} style={diceSize}>
          D8
        </button>
        <button onClick={() => handleRoll(6)} style={diceSize}>
          D6
        </button>
        <button onClick={() => handleRoll(4)} style={diceSize}>
          D4
        </button>
        <span>Result: {rollResult}</span>
        {/* ... Inventory section ... */}
      </div>
    </div>
  );
};
```


