                 // If dragging locally, ignore DB update for position
                 if (draggingTokenId === inc.id) {
                     const currentLocal = prev.find(p => p.id === inc.id);
                     return currentLocal || inc; 
                 }
                 return inc;
              });
              return merged;
          });

          setMapConfig({
              imageUrl: '',
              tokens: [],
