  },

  subscribeToCharacter: (charId: string, onUpdate: (newData: Character) => void): RealtimeChannel => {
    return supabase
      .channel(`char:${charId}`)
      .on('postgres_changes', 
        { event: 'UPDATE', schema: 'public', table: 'characters', filter: `id=eq.${charId}` }, 
        (payload) => {
          onUpdate(payload.new as Character);
        }
      )
      .subscribe();
  },

  // --- BROADCAST (MAP MOVEMENTS) ---
  // Connect to a volatile channel for High Frequency updates
  joinMapRoom: (campaignId: string, onTokenMove: (payload: any) => void): RealtimeChannel => {
      return supabase
          .channel(`map_room:${campaignId}`)
          .on('broadcast', { event: 'token_move' }, (payload) => {
              onTokenMove(payload.payload);
          })
          .subscribe();
  },

  // Add flip, rotation AND variant to payload
  sendTokenMove: (channel: RealtimeChannel, payload: { id: string, x: number, y: number, flip?: boolean, rotation?: number, variant?: number }) => {
      channel.send({
          type: 'broadcast',
          event: 'token_move',
          payload: payload
      });
  },

  leaveMapRoom: (channel: RealtimeChannel) => {
      supabase.removeChannel(channel);
  }
};
