import React, { useState, useEffect, useRef } from 'react';
import { Campaign, CharacterSummary, MapToken, MapConfig } from '../types';
import { dbService } from '../services/dbService';
import { Button } from './ui/Button';
import { Input } from './ui/Input';
import { Lock, Map as MapIcon, Plus, Eye, EyeOff, Save, Move, Skull, User, X, Check, ShieldAlert, Settings, Minus, Trash2, ArrowLeftRight, Layers } from 'lucide-react';
import { verifyPassword } from '../services/cryptoService';
import { RealtimeChannel } from '@supabase/supabase-js';

interface Props {
  campaign: Campaign;
  characters: CharacterSummary[];
  isAdmin: boolean;
  currentIdentity?: string;
}

export const MapView: React.FC<Props> = ({ campaign, characters, isAdmin, currentIdentity }) => {
  const [mapConfig, setMapConfig] = useState<MapConfig>({
      imageUrl: '',
      tokens: [],
      allowedPlayers: [],
      ...campaign.mapConfig
  });

  // Local state for dragging (immediate feedback)
  const [tokens, setTokens] = useState<MapToken[]>(mapConfig.tokens || []);

  // Identity State (For non-admins to "login" to map)
  const [selectedIdentity, setSelectedIdentity] = useState<string | null>(null);
  const [identityPassword, setIdentityPassword] = useState('');
  const [identityError, setIdentityError] = useState('');
  const [isIdentityVerified, setIsIdentityVerified] = useState(false);

  // Admin Tools State
  const [showTools, setShowTools] = useState(true);
  const [newMonster, setNewMonster] = useState({ name: '', url: '', isVisible: false });
  const [mapUrlInput, setMapUrlInput] = useState(mapConfig.imageUrl);

  // Dragging State
  const [draggingTokenId, setDraggingTokenId] = useState<string | null>(null);
  const mapRef = useRef<HTMLDivElement>(null);

  // Broadcast Channel
  const channelRef = useRef<RealtimeChannel | null>(null);
  const lastBroadcastTime = useRef<number>(0);

  // Join Broadcast Channel on Mount
  useEffect(() => {
      channelRef.current = dbService.joinMapRoom(campaign.id, (payload: { id: string, x: number, y: number, flip?: boolean, rotation?: number, variant?: number, visible?: boolean }) => {
          // Received move event from another user
          // Update local state ONLY if I am NOT currently dragging THAT specific token
          setTokens(prev => prev.map(t => {
              if (t.id === payload.id) {
                  return { 
                      ...t, 
                      x: payload.x, 
                      y: payload.y, 
                      flip: payload.flip, 
                      rotation: payload.rotation,
                      variant: payload.variant as 1|2|3,
                      visible: payload.visible
                  };
              }
              return t;
          }));
      });

      return () => {
          if (channelRef.current) {
              dbService.leaveMapRoom(channelRef.current);
          }
      };
  }, [campaign.id]);

  // Sync with prop updates (Database Realtime)
  useEffect(() => {
      if (campaign.mapConfig) {
          setTokens(prev => {
              const incomingTokens = campaign.mapConfig?.tokens || [];
              const merged = incomingTokens.map(inc => {
                 // If dragging locally, ignore DB update for position
                 if (draggingTokenId === inc.id) {
                     const currentLocal = prev.find(p => p.id === inc.id);
                     return currentLocal || inc; 
                 }
                 return inc;
              });
              return merged;
          });

          setMapConfig({
              imageUrl: '',
              tokens: [],
              allowedPlayers: [],
              ...campaign.mapConfig
          });
      }
  }, [campaign.mapConfig, draggingTokenId]);

  // NATIVE SCROLL LOCKING LOGIC + KEYBOARD VARIANT SWITCHING
  useEffect(() => {
      const mapEl = mapRef.current;
      if (!mapEl) return;

      const handleNativeWheel = (e: WheelEvent) => {
          if (draggingTokenId) {
              e.preventDefault(); // STOP PAGE SCROLL

              setTokens(prev => prev.map(t => {
                 if (t.id === draggingTokenId) {
                    const currentRot = t.rotation || 0;
                    // Scroll up/down changes rotation by 15 deg
                    const newRot = currentRot + (e.deltaY > 0 ? 15 : -15);
                    return { ...t, rotation: newRot };
                 }
                 return t;
              }));
          }
      };

      const handleKeyDown = (e: KeyboardEvent) => {
          if (draggingTokenId && ['1','2','3'].includes(e.key)) {
              const variant = parseInt(e.key) as 1|2|3;
              setTokens(prev => prev.map(t => {
                  if (t.id === draggingTokenId) {
                      return { ...t, variant: variant };
                  }
                  return t;
              }));
              // We don't force broadcast here, we let the mousemove interval handle it 
              // or we could force it. Let's keep it simple, MouseMove will pick it up.
          }
      }

      mapEl.addEventListener('wheel', handleNativeWheel, { passive: false });
      window.addEventListener('keydown', handleKeyDown);

      return () => {
          mapEl.removeEventListener('wheel', handleNativeWheel);
          window.removeEventListener('keydown', handleKeyDown);
      };
  }, [draggingTokenId]);

  // Auto-login if currentIdentity is provided
  useEffect(() => {
    if (currentIdentity) {
        setSelectedIdentity(currentIdentity);
        setIsIdentityVerified(true);
    }
  }, [currentIdentity]);

  // --- IDENTITY LOGIC (PLAYER) ---
  const handleIdentityLogin = async () => {
      if (!selectedIdentity) return;

      const char = await dbService.getCharacterById(selectedIdentity);
      if (char && await verifyPassword(identityPassword, char.passwordHash)) {
          setIsIdentityVerified(true);
          setIdentityError('');
      } else {
          setIdentityError('Senha incorreta.');
      }
  };

  // --- DRAG LOGIC ---
  const handleMouseDown = (e: React.MouseEvent, tokenId: string) => {
      // Permission Check:
      // Admin moves anyone. Player moves ONLY themselves.
      if (!isAdmin) {
          if (tokenId !== selectedIdentity) return;
      }

      e.preventDefault();
      setDraggingTokenId(tokenId);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
      if (!draggingTokenId || !mapRef.current) return;

      const rect = mapRef.current.getBoundingClientRect();
      const x = ((e.clientX - rect.left) / rect.width) * 100;
      const y = ((e.clientY - rect.top) / rect.height) * 100;

      const clampedX = Math.max(0, Math.min(100, x));
      const clampedY = Math.max(0, Math.min(100, y));

      const currentToken = tokens.find(t => t.id === draggingTokenId);

      // 1. Update local state immediately (Visual feedback)
      setTokens(prev => prev.map(t => 
          t.id === draggingTokenId 
            ? { ...t, x: clampedX, y: clampedY } 
